data sasep.out;
   dcl package score _EH2UOJ4CUQFHZTDBN8GC66TR4();
   dcl double EM_EVENTPROBABILITY;
   dcl double "CLAGE";
   dcl nchar(12) EM_CLASSIFICATION;
