   dcl double "P_BAD1" having label n'Predicted: BAD=1';
   dcl double "P_BAD0" having label n'Predicted: BAD=0';
   dcl nchar(12) "I_BAD" having label n'Into: BAD';
   dcl nchar(4) "_WARN_" having label n'Warnings';
   varlist allvars [_all_];


   method init();
      _EH2UOJ4CUQFHZTDBN8GC66TR4.setvars(allvars);
      _EH2UOJ4CUQFHZTDBN8GC66TR4.setkey(n'3BC83821DC51C15351923DE50043F841F06B3F42');
   end;

   method post_EH2UOJ4CUQFHZTDBN8GC66TR4();
      dcl double _P_;

      if "P_BAD0" = . then "P_BAD0" = 0.8005033557;
      if "P_BAD1" = . then "P_BAD1" = 0.1994966443;
      if MISSING("I_BAD") then do ;
      _P_ = 0.0;
      if "P_BAD1" > _P_ then do ;
      _P_ = "P_BAD1";
      "I_BAD" = '           1';
      end;
      if "P_BAD0" > _P_ then do ;
      _P_ = "P_BAD0";
      "I_BAD" = '           0';
      end;
      end;
      EM_EVENTPROBABILITY = "P_BAD1";
      EM_CLASSIFICATION = "I_BAD";
      EM_PROBABILITY = MAX("P_BAD0", "P_BAD1");

   end;


   method run();
      set SASEP.IN;
      _EH2UOJ4CUQFHZTDBN8GC66TR4.scoreRecord();
      post_EH2UOJ4CUQFHZTDBN8GC66TR4();
   end;

   method term();
   end;

enddata;
 endpackage;
